    def update_labeltext(self):

        self.label.setText(self._translate(
            "MainWindow", "<html><head/><body><p><span style=\" font-size:48pt;\">"+self.text+"</span></p></body></html>"))

    def set_labeltext(self,text):
        
        if text == 'del':

            self.text = self.text[:-1]

            if len(self.text) == 0:
                self.text = '0' 

            self.update_labeltext()

        else:
            
            if self.text[0] == '0' :
                self.text = text
            else:
                self.text += text

            self.update_labeltext()

        
       
        

